mapper.select:

  select  * from boards 
  where bbs_id =  #{bbs_id}



         and title like concat('%' , trim(#{searchinfo}), '%' )  	   


         and writer like concat('%' , trim(#{searchinfo}), '%' )  	   


         and contents like concat('%' , trim(#{searchinfo}), '%' )  	   


order by story_id asc  limit  ${startPage} ,  ${limitPage}

mapper.update:

update boards
   set  comp_name       =     #{comp_name}
        ,comp_no        =     #{comp_no}
        ,contents       =     #{contents} 
        ,notice         =     #{notice}
        ,title          =     #{title}
        ,updated_at     =     now()
where   bbs_id          =     #{bbs_id}
and     story_id        =     #{story_id}


update boards
   set      view       =     view    + 1 
 where   bbs_id        =     #{bbs_id}
 and     story_id        =     #{story_id}

mapper.delete:

delete from boards
 where   bbs_id        =     #{bbs_id}
   and   story_id      =     #{story_id}
