mapper.select:

select loginfailcount ,lockyn from  
users 
WHERE userid =  #{userid}


select * from  
users 
WHERE userid =  #{userid}  and password = #{password}


select count(1) ,today_visit ,total_visit ,updated_at from  
users 
WHERE userid =  #{userid}  and password = #{password}

mapper.update:

update users
   set   comp_name        =     #{comp_name} 
        ,comp_no          =     #{comp_no}
        ,updated_at       =     now()
        ,email            =     #{email}
        ,endtime          =     #{endtime}
        ,ipaddr           =     #{ipaddr}
        ,name             =     #{name}
        ,phonenumber      =     #{phonenumber}
        ,starttime        =     #{starttime}
        ,user_level       =     #{user_level}
where   userid            =     #{userid}
